import React, { useState, useEffect } from 'react';
import './App.css';

// Tab Components
import HomeTab from './components/tabs/HomeTab';
import TimerTab from './components/tabs/TimerTab';
import ScheduleTab from './components/tabs/ScheduleTab';
import ClientsTab from './components/tabs/ClientsTab';
import EntriesTab from './components/tabs/EntriesTab';
import InvoiceTab from './components/tabs/InvoiceTab';

// UI Components
import Navigation from './components/ui/Navigation';

// Hooks
import { useLocalStorage } from './hooks/useLocalStorage';

// Utils
import { dataService } from './utils/dataService';

function App() {
  // Active tab state
  const [activeTab, setActiveTab] = useState('home');
  
  // Main application data state
  const [appData, setAppData] = useLocalStorage('mechanicHoursData', {
    clients: [],
    tasks: [],
    entries: [],
    scheduledJobs: [],
    active: null
  });

  // Update app data function
  const updateAppData = (newData) => {
    setAppData(prevData => ({ ...prevData, ...newData }));
  };

  // Navigation tabs configuration
  const tabs = [
    { id: 'home', label: 'Home', icon: 'üè†' },
    { id: 'timer', label: 'Timer', icon: '‚è±Ô∏è' },
    { id: 'schedule', label: 'Schedule', icon: 'üìÖ' },
    { id: 'clients', label: 'Clients', icon: 'üë•' },
    { id: 'entries', label: 'Entries', icon: 'üìã' },
    { id: 'invoice', label: 'Invoice', icon: 'üìÑ' }
  ];

  return (
    <div className="min-h-screen bg-gray-900">
      {/* Header */}
      <header className="bg-gray-800 text-white p-4 border-b border-gray-700">
        <h1 className="text-xl font-bold text-center">Mechanic Hours</h1>
        <p className="text-gray-400 text-sm text-center mt-1">Time Tracking & Invoicing</p>
      </header>

      {/* Main Content */}
      <main className="pb-20 px-3 py-4 max-w-mobile mx-auto">
        {activeTab === 'home' && (
          <HomeTab 
            appData={appData} 
            updateAppData={updateAppData}
            onNavigate={setActiveTab}
          />
        )}
        {activeTab === 'timer' && (
          <TimerTab 
            appData={appData} 
            updateAppData={updateAppData} 
          />
        )}
        {activeTab === 'schedule' && (
          <ScheduleTab 
            appData={appData} 
            updateAppData={updateAppData} 
          />
        )}
        {activeTab === 'clients' && (
          <ClientsTab 
            appData={appData} 
            updateAppData={updateAppData} 
          />
        )}
        {activeTab === 'entries' && (
          <EntriesTab 
            appData={appData} 
            updateAppData={updateAppData} 
          />
        )}
        {activeTab === 'invoice' && (
          <InvoiceTab 
            appData={appData} 
            updateAppData={updateAppData} 
          />
        )}
      </main>

      {/* Bottom Navigation */}
      <Navigation 
        tabs={tabs}
        activeTab={activeTab}
        onTabChange={setActiveTab}
      />
    </div>
  );
}

export default App;
