import React from 'react';
import Card from '../ui/Card';
import Button from '../ui/Button';
import { dataService } from '../../utils/dataService';

/**
 * Home/Dashboard Tab Component
 * Shows weekly schedule, recent entries, and outstanding invoices
 */
const HomeTab = ({ appData, updateAppData, onNavigate }) => {
  // Get current date info
  const now = new Date();
  const startOfWeek = new Date(now);
  startOfWeek.setDate(now.getDate() - now.getDay()); // Start of week (Sunday)
  
  const endOfWeek = new Date(startOfWeek);
  endOfWeek.setDate(startOfWeek.getDate() + 6); // End of week (Saturday)

  // Get scheduled jobs for this week
  const thisWeekScheduled = appData.scheduledJobs?.filter(job => {
    const jobDate = new Date(job.scheduledDate);
    return jobDate >= startOfWeek && jobDate <= endOfWeek;
  }) || [];

  // Get recent completed entries (last 5)
  const recentEntries = appData.entries
    .filter(entry => entry.end)
    .sort((a, b) => b.end - a.end)
    .slice(0, 5);

  // Calculate outstanding invoices (entries without invoices)
  const outstandingEntries = appData.entries.filter(entry => entry.end && !entry.invoiced);
  const outstandingAmount = dataService.calculateTotalAmount(outstandingEntries, appData.clients);

  // Start timer from scheduled job
  const startFromSchedule = (scheduledJob) => {
    const newEntry = {
      id: dataService.generateId(),
      clientId: scheduledJob.clientId,
      taskName: scheduledJob.taskName,
      start: Date.now(),
      end: null,
      notes: scheduledJob.notes || '',
      scheduledJobId: scheduledJob.id
    };

    updateAppData({
      entries: [...appData.entries, newEntry],
      active: { entryId: newEntry.id }
    });

    // Navigate to timer tab
    onNavigate('timer');
  };

  // Get days of the week for schedule display
  const weekDays = [];
  for (let i = 0; i < 7; i++) {
    const day = new Date(startOfWeek);
    day.setDate(startOfWeek.getDate() + i);
    weekDays.push(day);
  }

  return (
    <div className="space-y-4">
      {/* Quick Stats */}
      <Card>
        <h2 className="text-xl font-bold mb-3 text-gray-200">Dashboard</h2>
        <div className="grid grid-cols-3 gap-4 text-center">
          <div>
            <div className="text-2xl font-bold text-red-400">
              {thisWeekScheduled.length}
            </div>
            <div className="text-sm text-gray-400">This Week</div>
          </div>
          <div>
            <div className="text-2xl font-bold text-blue-400">
              {recentEntries.length}
            </div>
            <div className="text-sm text-gray-400">Recent Jobs</div>
          </div>
          <div>
            <div className="text-2xl font-bold text-green-400">
              {dataService.formatCurrency(outstandingAmount)}
            </div>
            <div className="text-sm text-gray-400">Outstanding</div>
          </div>
        </div>
      </Card>

      {/* This Week's Schedule */}
      <Card>
        <div className="flex justify-between items-center mb-3">
          <h3 className="text-lg font-semibold text-gray-200">This Week's Schedule</h3>
          <Button 
            variant="secondary" 
            size="small"
            onClick={() => onNavigate('schedule')}
          >
            + Add Job
          </Button>
        </div>
        
        {thisWeekScheduled.length === 0 ? (
          <div className="text-center py-4 text-gray-400">
            No jobs scheduled this week
          </div>
        ) : (
          <div className="space-y-2">
            {weekDays.map(day => {
              const dayJobs = thisWeekScheduled.filter(job => {
                const jobDate = new Date(job.scheduledDate);
                return jobDate.toDateString() === day.toDateString();
              });

              return (
                <div key={day.toDateString()}>
                  <div className="text-sm font-medium text-gray-300 mb-1">
                    {day.toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' })}
                  </div>
                  {dayJobs.length === 0 ? (
                    <div className="text-xs text-gray-500 ml-4">No jobs scheduled</div>
                  ) : (
                    <div className="space-y-1">
                      {dayJobs.map(job => {
                        const client = appData.clients.find(c => c.id === job.clientId);
                        const isToday = day.toDateString() === now.toDateString();
                        
                        return (
                          <div 
                            key={job.id} 
                            className={`p-2 rounded border-l-4 ${
                              isToday ? 'border-red-500 bg-gray-700' : 'border-blue-500 bg-gray-800'
                            }`}
                          >
                            <div className="flex justify-between items-start">
                              <div className="flex-1">
                                <div className="font-medium text-gray-200">
                                  {client?.name} - {job.taskName}
                                </div>
                                <div className="text-sm text-gray-400">
                                  {job.scheduledTime} ‚Ä¢ {job.estimatedHours}h
                                </div>
                                {job.notes && (
                                  <div className="text-xs text-gray-500 mt-1">
                                    {job.notes}
                                  </div>
                                )}
                              </div>
                              {isToday && (
                                <Button
                                  size="small"
                                  special="start"
                                  onClick={() => startFromSchedule(job)}
                                >
                                  Start
                                </Button>
                              )}
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </Card>

      {/* Recent Activity */}
      <Card>
        <div className="flex justify-between items-center mb-3">
          <h3 className="text-lg font-semibold text-gray-200">Recent Activity</h3>
          <Button 
            variant="secondary" 
            size="small"
            onClick={() => onNavigate('entries')}
          >
            View All
          </Button>
        </div>
        
        {recentEntries.length === 0 ? (
          <div className="text-center py-4 text-gray-400">
            No recent activity
          </div>
        ) : (
          <div className="space-y-2">
            {recentEntries.map(entry => {
              const client = appData.clients.find(c => c.id === entry.clientId);
              const duration = entry.end - entry.start;
              
              return (
                <div key={entry.id} className="border-l-4 border-green-500 pl-3 py-2 bg-gray-700 rounded">
                  <div className="font-medium text-gray-200">
                    {client?.name} - {entry.taskName || 'Unknown Task'}
                  </div>
                  <div className="text-sm text-gray-400">
                    {dataService.formatDate(entry.start)} ‚Ä¢ {dataService.formatDuration(duration)}
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </Card>

      {/* Outstanding Invoices */}
      {outstandingAmount > 0 && (
        <Card>
          <div className="flex justify-between items-center">
            <div>
              <h3 className="text-lg font-semibold text-gray-200">Outstanding Work</h3>
              <p className="text-sm text-gray-400">
                {outstandingEntries.length} completed jobs not yet invoiced
              </p>
            </div>
            <div className="text-right">
              <div className="text-xl font-bold text-green-400">
                {dataService.formatCurrency(outstandingAmount)}
              </div>
              <Button 
                variant="secondary" 
                size="small"
                onClick={() => onNavigate('invoice')}
              >
                Create Invoice
              </Button>
            </div>
          </div>
        </Card>
      )}

      {/* Quick Actions */}
      <Card>
        <h3 className="text-lg font-semibold mb-3 text-gray-200">Quick Actions</h3>
        <div className="grid grid-cols-2 gap-3">
          <Button onClick={() => onNavigate('timer')}>
            ‚è±Ô∏è Start Timer
          </Button>
          <Button onClick={() => onNavigate('schedule')} variant="secondary">
            üìÖ Schedule Job
          </Button>
          <Button onClick={() => onNavigate('clients')} variant="secondary">
            üë• Add Client
          </Button>
          <Button onClick={() => onNavigate('invoice')} variant="secondary">
            üìÑ Create Invoice
          </Button>
        </div>
      </Card>
    </div>
  );
};

export default HomeTab;
