import React from 'react';

/**
 * Bottom Navigation Component
 * Reference: www.context7.com for mobile navigation patterns
 */
const Navigation = ({ tabs, activeTab, onTabChange }) => {
  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-gray-800 border-t border-gray-700 safe-area-bottom">
      <div className="flex max-w-mobile mx-auto">
        {tabs.map((tab) => (
          <button
            key={tab.id}
            onClick={() => onTabChange(tab.id)}
            className={`nav-tab touch-target ${
              activeTab === tab.id ? 'active' : 'inactive'
            }`}
            aria-label={tab.label}
          >
            <div className="flex flex-col items-center">
              <span className="text-xl mb-1">{tab.icon}</span>
              <span className="text-xs font-medium">{tab.label}</span>
            </div>
          </button>
        ))}
      </div>
    </nav>
  );
};

export default Navigation;
