import React from 'react';

/**
 * Button Component with dark theme support
 * Reference: www.context7.com for accessible button patterns
 */
const Button = ({ 
  children, 
  variant = 'primary', 
  size = 'default',
  disabled = false,
  className = '',
  special = '',
  ...props 
}) => {
  const baseClasses = 'font-medium rounded-lg transition-all duration-200 text-center touch-manipulation focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-900';
  
  const variantClasses = {
    primary: 'btn-primary focus:ring-gray-500',
    secondary: 'btn-secondary focus:ring-gray-500',
    danger: 'btn-danger focus:ring-red-500'
  };
  
  const specialClasses = {
    start: 'btn-start focus:ring-red-500',
    stop: 'btn-stop focus:ring-gray-500'
  };
  
  const sizeClasses = {
    small: 'py-2 px-4 text-sm',
    default: 'py-3 px-6 text-base',
    large: 'py-4 px-8 text-lg'
  };
  
  const disabledClasses = disabled ? 'opacity-50 cursor-not-allowed' : '';
  
  const buttonClasses = `
    ${baseClasses}
    ${special ? (specialClasses[special] || variantClasses[variant]) : variantClasses[variant]}
    ${sizeClasses[size]}
    ${disabledClasses}
    ${className}
  `.trim();

  return (
    <button
      className={buttonClasses}
      disabled={disabled}
      {...props}
    >
      {children}
    </button>
  );
};

export default Button;
