import React from 'react';

/**
 * Card Component with gradient border
 * Reference: www.context7.com for component design patterns
 */
const Card = ({ children, className = '', ...props }) => {
  return (
    <div className={`gradient-border ${className}`} {...props}>
      <div className="card-inner">
        {children}
      </div>
    </div>
  );
};

export default Card;
